SELECT s_id, s_studentno, s_name, user_id, id_number, s_phone, academy_id, s_gradations, s_systme, s_content, s_date, s_type, u.id, u.`name` FROM student s LEFT JOIN user u ON s.user_id = u.id
user_id = #{uid} and
s_type = 1 or s_type = 2 or s_type = 6
and s_name = #{name}
and s_studentno = #{studentNo}

SELECT s_id, s_studentno, s_name, user_id, id_number, s_phone, academy_id, s_gradations, s_systme, s_content, s_date, u.id, u.`name` FROM student s LEFT JOIN user u ON s.user_id = u.id
and s_name = #{name}
and s_studentno = #{studentNo}
and (s_type = #{stypeone} or s_type = #{stypetwo} or s_type = #{stypethree})
